mapper.select:
SELECT


,minsu.shangjia_id as minsuShangjiaId
,minsu.minsu_name as minsuName
,minsu.minsu_photo as minsuPhoto
,minsu.minsu_types as minsuTypes
,minsu.minsu_new_money as minsuNewMoney
,minsu.minsu_clicknum as minsuClicknum
,minsu.shangxia_types as shangxiaTypes
,minsu.minsu_delete as minsuDelete
,minsu.minsu_content as minsuContent
,yonghu.yonghu_name as yonghuName
,yonghu.yonghu_phone as yonghuPhone
,yonghu.yonghu_id_number as yonghuIdNumber
,yonghu.yonghu_photo as yonghuPhoto
,yonghu.yonghu_email as yonghuEmail
,yonghu.new_money as newMoney

FROM minsu_collection  a
left JOIN minsu minsu ON a.minsu_id = minsu.id
left JOIN yonghu yonghu ON a.yonghu_id = yonghu.id



        and a.id in

        #{item}



        and (
            a.minsu_id = #{params.minsuId}
        )


        and (
            a.yonghu_id = #{params.yonghuId}
        )


        and a.minsu_collection_types = #{params.minsuCollectionTypes}


          and UNIX_TIMESTAMP(a.insert_time) >= UNIX_TIMESTAMP(#{params.insertTimeStart}) 


          and UNIX_TIMESTAMP(a.insert_time) <= UNIX_TIMESTAMP(#{params.insertTimeEnd}) 




        and a.minsu_id IS NOT NULL


        and minsu.shangjia_id = #{params.shangjiaId}


        and minsu.minsu_name like CONCAT('%',#{params.minsuName},'%')


        and minsu.minsu_types = #{params.minsuTypes}



          and minsu.minsu_new_money >= #{params.minsuNewMoneyStart}   


          and minsu.minsu_new_money <= #{params.minsuNewMoneyEnd}   


          and minsu.minsu_clicknum >= #{params.minsuClicknumStart}   


          and minsu.minsu_clicknum <= #{params.minsuClicknumEnd}   


        and minsu.minsu_clicknum = #{params.minsuClicknum}


        and minsu.shangxia_types = #{params.shangxiaTypes}



          and minsu.minsu_delete >= #{params.minsuDeleteStart}   


          and minsu.minsu_delete <= #{params.minsuDeleteEnd}   


        and minsu.minsu_delete = #{params.minsuDelete}


        and minsu.minsu_content like CONCAT('%',#{params.minsuContent},'%')



        and a.yonghu_id IS NOT NULL


        and yonghu.yonghu_name like CONCAT('%',#{params.yonghuName},'%')


        and yonghu.yonghu_phone like CONCAT('%',#{params.yonghuPhone},'%')


        and yonghu.yonghu_id_number like CONCAT('%',#{params.yonghuIdNumber},'%')


        and yonghu.yonghu_email like CONCAT('%',#{params.yonghuEmail},'%')


          and yonghu.new_money >= #{params.newMoneyStart}   


          and yonghu.new_money <= #{params.newMoneyEnd}   


order by a.${params.sort} ${params.order}
